import React, { useState } from 'react';
import './LoginPage.css';

const LoginPage = ({ onLogin }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [isSignUp, setIsSignUp] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Simple authentication - in real app, this would call an API
    if (formData.email && formData.password) {
      onLogin('dummy-token');
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h1 className="login-title">Login</h1>
        
        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="email" className="form-label">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="form-control"
              placeholder="Enter email"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password" className="form-label">Password:</label>
            <input
              type={showPassword ? 'text' : 'password'}
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className="form-control"
              placeholder="Enter password"
              required
            />
          </div>

          <div className="checkbox-group">
            <input
              type="checkbox"
              id="showPassword"
              checked={showPassword}
              onChange={(e) => setShowPassword(e.target.checked)}
              className="checkbox"
            />
            <label htmlFor="showPassword" className="checkbox-label">Show Password</label>
          </div>

          <button type="submit" className="signin-btn">
            SIGN IN
          </button>
        </form>

        <div className="login-links">
          <p className="forgot-link">
            Forgot <span className="link-text">Username / Password</span>?
          </p>
          <p className="signup-link">
            Don't have an account? <span className="link-text" onClick={() => setIsSignUp(!isSignUp)}>Sign up</span>
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
