import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import LoginPage from './components/LoginPage';
import ActivityTracker from './components/ActivityTracker';
import AdminDashboard from './components/AdminDashboard';
import { useState, useEffect } from 'react';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRole, setUserRole] = useState('employee');

  useEffect(() => {
    // Check if user is logged in (you can implement proper authentication later)
    const token = localStorage.getItem('authToken');
    const role = localStorage.getItem('userRole') || 'employee';
    if (token) {
      setIsAuthenticated(true);
      setUserRole(role);
    }
  }, []);

  const handleLogin = (token, role = 'employee') => {
    localStorage.setItem('authToken', token);
    localStorage.setItem('userRole', role);
    setIsAuthenticated(true);
    setUserRole(role);
  };

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userRole');
    setIsAuthenticated(false);
    setUserRole('employee');
  };

  return (
    <Router future={{ v7_startTransition: true, v7_relativeSplatPath: true }}>
      <div className="App">
        <Routes>
          <Route 
            path="/" 
            element={
              isAuthenticated ? 
                (userRole === 'admin' ? 
                  <Navigate to="/admin" replace /> : 
                  <Navigate to="/suivi-des-activity" replace />
                ) : 
                <LoginPage onLogin={handleLogin} />
            } 
          />
          <Route 
            path="/suivi-des-activity" 
            element={
              isAuthenticated && userRole === 'employee' ? 
                <ActivityTracker onLogout={handleLogout} /> : 
                <Navigate to="/" replace />
            } 
          />
          <Route 
            path="/admin" 
            element={
              isAuthenticated && userRole === 'admin' ? 
                <AdminDashboard onLogout={handleLogout} /> : 
                <Navigate to="/" replace />
            } 
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
