import React, { useState, useEffect } from 'react';
import './ActivityTracker.css';

const ActivityTracker = ({ onLogout }) => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [clientActivities, setClientActivities] = useState([{ client: '', activity: '' }]);
  const [absenceData, setAbsenceData] = useState({
    type: '',
    telework: '0',
    restaurantTicket: '0'
  });
  const [monthData, setMonthData] = useState(() => {
    // Load data from localStorage on component mount
    const saved = localStorage.getItem('monthData');
    return saved ? JSON.parse(saved) : {};
  });
  const [forceUpdate, setForceUpdate] = useState(0);

  // Debug: Log monthData changes
  useEffect(() => {
    console.log('Month data updated:', monthData);
    // Save to localStorage whenever monthData changes
    localStorage.setItem('monthData', JSON.stringify(monthData));
  }, [monthData]);

  const clientOptions = [
    'Bouygues', 'AFD', 'Orange', 'Free', 'SFR', 'Ericson', 'TDF', 'SNCF',
    'Autres clients France', 'ARCEP Burkina', 'Orange Sénégal', 'ARCEP Togo',
    'Togocell - YAS', 'Lillybelle Togo', 'ARTP Sénégal', 'Autres clients export'
  ];

  const activityOptions = [
    'Support de maintenance', 'Service projet', 'Avant-vente', 
    'Supply chain', 'Commercial', 'interne'
  ];

  const absenceTypes = ['Congés', 'Maladie', 'Récupération', 'Formation', 'Présent'];

  const monthNames = [
    'Janvier', 'Février', 'Mars', 'Avril', 'Mai', 'Juin',
    'Juillet', 'Août', 'Septembre', 'Octobre', 'Novembre', 'Décembre'
  ];

  const dayNames = ['Lun', 'Mar', 'Mer', 'Jeu', 'Ven', 'Sam', 'Dim'];

  const navigateMonth = (direction) => {
    setCurrentDate(prev => {
      const newDate = new Date(prev);
      if (direction === 'prev') {
        newDate.setMonth(newDate.getMonth() - 1);
      } else {
        newDate.setMonth(newDate.getMonth() + 1);
      }
      return newDate;
    });
  };

  const addClientActivity = () => {
    setClientActivities([...clientActivities, { client: '', activity: '' }]);
  };

  const removeClientActivity = (index) => {
    if (clientActivities.length > 1) {
      setClientActivities(clientActivities.filter((_, i) => i !== index));
    }
  };

  const updateClientActivity = (index, field, value) => {
    const updated = [...clientActivities];
    updated[index][field] = value;
    setClientActivities(updated);
  };

  const handleAbsenceChange = (field, value) => {
    setAbsenceData(prev => ({ ...prev, [field]: value }));
  };

  const generateMonthCalendar = () => {
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    const startingDayOfWeek = (firstDay.getDay() + 6) % 7; // Convert Sunday=0 to Monday=0

    const calendar = [];
    const today = new Date();

    // Add empty cells for days before the first day of the month
    for (let i = 0; i < startingDayOfWeek; i++) {
      calendar.push({ day: null, isWeekend: false, isToday: false });
    }

    // Add all days of the month
    for (let day = 1; day <= daysInMonth; day++) {
      const date = new Date(year, month, day);
      const dayOfWeek = date.getDay();
      const isWeekend = dayOfWeek === 0 || dayOfWeek === 6; // Sunday or Saturday
      const isToday = date.toDateString() === today.toDateString();
      
      // Use a more reliable date format that avoids timezone issues
      const dateString = `${year}-${String(month + 1).padStart(2, '0')}-${String(day).padStart(2, '0')}`;
      
      calendar.push({
        day,
        isWeekend,
        isToday,
        date: dateString
      });
    }

    console.log('Generated calendar for month:', month + 1, year);
    console.log('Calendar dates:', calendar.filter(d => d.day).map(d => `${d.day}: ${d.date}`));
    return calendar;
  };

  const calculateDayValue = (clientCount) => {
    if (clientCount === 0) return [];
    if (clientCount === 1) return [1];
    
    const value = 1 / clientCount;
    return Array(clientCount).fill(value);
  };

  const handleSubmit = () => {
    // Validate client activities
    const validActivities = clientActivities.filter(ca => ca.client && ca.activity);
    if (validActivities.length === 0) {
      alert('Veuillez sélectionner au moins un client et une activité');
      return;
    }

    // Validate absence data
    if (!absenceData.type) {
      alert('Veuillez sélectionner un type d\'absence');
      return;
    }

    const dayValue = calculateDayValue(validActivities.length);
    const today = new Date().toISOString().split('T')[0];
    
    console.log('Submitting data for:', today);
    console.log('Valid activities:', validActivities);
    console.log('Day values:', dayValue);
    
    // Update existing data for the day or create new
    setMonthData(prev => {
      const existingData = prev[today] || { activities: [], absence: {}, dayValue: [] };
      
      // Merge with existing activities (add new ones)
      const allActivities = [...existingData.activities, ...validActivities];
      const allDayValue = calculateDayValue(allActivities.length);
      
      const newData = {
        ...prev,
        [today]: {
          activities: allActivities,
          absence: absenceData,
          dayValue: allDayValue
        }
      };
      
      console.log('New month data:', newData);
      return newData;
    });

    // Reset only the form inputs, not the entire form
    setClientActivities([{ client: '', activity: '' }]);
    setAbsenceData({ type: '', telework: '0', restaurantTicket: '0' });
    
    // Force re-render
    setForceUpdate(prev => prev + 1);
    
    // Show success message
    alert(`Données enregistrées pour le ${today}. Valeurs: ${dayValue.join(', ')}`);
  };

  const getDayData = (date) => {
    const data = monthData[date] || null;
    if (data) {
      console.log(`✅ Found data for ${date}:`, data);
    } else {
      console.log(`❌ No data found for ${date}. Available dates:`, Object.keys(monthData));
    }
    return data;
  };

  const calendar = generateMonthCalendar();

  return (
    <div className="activity-tracker">
      <header className="header">
        <h1>Suivi des Activités</h1>
        <button onClick={onLogout} className="logout-btn">Déconnexion</button>
      </header>

      <div className="month-navigation">
        <button onClick={() => navigateMonth('prev')} className="nav-btn">
          Précédent
        </button>
        <h2 className="month-year">
          {monthNames[currentDate.getMonth()]} {currentDate.getFullYear()}
        </h2>
        <button onClick={() => navigateMonth('suivant')} className="nav-btn">
          Suivant
        </button>
      </div>

      <div className="content">
        <h3 className="section-title">Temps Standards</h3>
        
        {/* Client/Activity Table */}
        <div className="table-section">
          <h4>Clients et Activités</h4>
          <table className="data-table">
            <thead>
              <tr>
                <th>Client</th>
                <th>Activité</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {clientActivities.map((item, index) => (
                <tr key={index}>
                  <td>
                    <select
                      value={item.client}
                      onChange={(e) => updateClientActivity(index, 'client', e.target.value)}
                      className="form-control"
                      required
                    >
                      <option value="">Sélectionner un client</option>
                      {clientOptions.map(client => (
                        <option key={client} value={client}>{client}</option>
                      ))}
                    </select>
                  </td>
                  <td>
                    <select
                      value={item.activity}
                      onChange={(e) => updateClientActivity(index, 'activity', e.target.value)}
                      className="form-control"
                      required
                    >
                      <option value="">Sélectionner une activité</option>
                      {activityOptions.map(activity => (
                        <option key={activity} value={activity}>{activity}</option>
                      ))}
                    </select>
                  </td>
                  <td>
                    {clientActivities.length > 1 && (
                      <button
                        onClick={() => removeClientActivity(index)}
                        className="remove-btn"
                      >
                        Supprimer
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <button onClick={addClientActivity} className="add-btn">
            Ajouter une ligne
          </button>
        </div>

        {/* Absence/Telework/Restaurant Table */}
        <div className="table-section">
          <h4>Absences et Autres</h4>
          <table className="data-table">
            <thead>
              <tr>
                <th>Type d'absence</th>
                <th>Télétravail</th>
                <th>Ticket restaurant</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>
                  <select
                    value={absenceData.type}
                    onChange={(e) => handleAbsenceChange('type', e.target.value)}
                    className="form-control"
                    required
                  >
                    <option value="">Sélectionner</option>
                    {absenceTypes.map(type => (
                      <option key={type} value={type}>{type}</option>
                    ))}
                  </select>
                </td>
                <td>
                  <select
                    value={absenceData.telework}
                    onChange={(e) => handleAbsenceChange('telework', e.target.value)}
                    className="form-control"
                  >
                    <option value="0">0</option>
                    <option value="1">1</option>
                  </select>
                </td>
                <td>
                  <select
                    value={absenceData.restaurantTicket}
                    onChange={(e) => handleAbsenceChange('restaurantTicket', e.target.value)}
                    className="form-control"
                  >
                    <option value="0">0</option>
                    <option value="1">1</option>
                  </select>
                </td>
              </tr>
            </tbody>
          </table>
        </div>

        <div className="button-group">
          <button onClick={handleSubmit} className="submit-btn">
            Soumettre
          </button>
          <button onClick={() => {
            // Add some test data for demonstration
            const today = new Date().toISOString().split('T')[0];
            const yesterday = new Date();
            yesterday.setDate(yesterday.getDate() - 1);
            const yesterdayStr = yesterday.toISOString().split('T')[0];
            
            console.log('Adding test data for:', today, yesterdayStr);
            
            setMonthData(prev => {
              const newData = {
                ...prev,
                [today]: {
                  activities: [
                    { client: 'Bouygues', activity: 'Support de maintenance' },
                    { client: 'Orange', activity: 'Service projet' }
                  ],
                  absence: { type: 'Présent', telework: '0', restaurantTicket: '1' },
                  dayValue: [0.5, 0.5]
                },
                [yesterdayStr]: {
                  activities: [
                    { client: 'AFD', activity: 'Avant-vente' },
                    { client: 'Free', activity: 'Commercial' },
                    { client: 'SFR', activity: 'Supply chain' },
                    { client: 'Ericson', activity: 'interne' }
                  ],
                  absence: { type: 'Présent', telework: '1', restaurantTicket: '0' },
                  dayValue: [0.25, 0.25, 0.25, 0.25]
                }
              };
              console.log('Test data added:', newData);
              return newData;
            });
            alert('Données de test ajoutées: Aujourd\'hui (2 clients = 0.5+0.5), Hier (4 clients = 0.25+0.25+0.25+0.25)');
          }} className="test-btn">
            Ajouter données de test
          </button>
          <button onClick={() => {
            setMonthData({});
            localStorage.removeItem('monthData');
            setForceUpdate(prev => prev + 1);
            alert('Données effacées');
          }} className="clear-btn">
            Effacer données
          </button>
          <button onClick={() => {
            setForceUpdate(prev => prev + 1);
            alert('Calendrier actualisé');
          }} className="refresh-btn">
            Actualiser
          </button>
          <button onClick={() => {
            console.log('Current monthData:', monthData);
            console.log('Current date:', new Date().toISOString().split('T')[0]);
            alert(`Données actuelles: ${JSON.stringify(monthData, null, 2)}`);
          }} className="debug-btn">
            Debug Data
          </button>
          <button onClick={() => {
            const today = new Date().toISOString().split('T')[0];
            setMonthData(prev => ({
              ...prev,
              [today]: {
                activities: [{ client: 'Test', activity: 'Test' }],
                absence: { type: 'Présent', telework: '0', restaurantTicket: '0' },
                dayValue: [1]
              }
            }));
            setForceUpdate(prev => prev + 1);
            alert(`Test data added for today (${today})`);
          }} className="test-today-btn">
            Test Today
          </button>
          <button onClick={() => {
            // Force add data for specific dates
            setMonthData(prev => ({
              ...prev,
              '2025-10-23': {
                activities: [{ client: 'Test23', activity: 'Test23' }],
                absence: { type: 'Présent', telework: '0', restaurantTicket: '0' },
                dayValue: [1]
              },
              '2025-10-24': {
                activities: [{ client: 'Test24', activity: 'Test24' }],
                absence: { type: 'Présent', telework: '0', restaurantTicket: '0' },
                dayValue: [1]
              }
            }));
            setForceUpdate(prev => prev + 1);
            alert('Test data added for Oct 23 and 24');
          }} className="test-specific-btn">
            Test Oct 23-24
          </button>
          <button onClick={() => {
            // Check what day of week Oct 23, 2025 is
            const oct23 = new Date(2025, 9, 23); // Month is 0-indexed, so 9 = October
            const oct24 = new Date(2025, 9, 24);
            console.log('Oct 23, 2025 day of week:', oct23.getDay(), '(0=Sunday, 6=Saturday)');
            console.log('Oct 24, 2025 day of week:', oct24.getDay(), '(0=Sunday, 6=Saturday)');
            console.log('Oct 23 is weekend?', oct23.getDay() === 0 || oct23.getDay() === 6);
            console.log('Oct 24 is weekend?', oct24.getDay() === 0 || oct24.getDay() === 6);
            alert(`Oct 23 is day ${oct23.getDay()} (${oct23.getDay() === 0 || oct23.getDay() === 6 ? 'WEEKEND' : 'WEEKDAY'})\nOct 24 is day ${oct24.getDay()} (${oct24.getDay() === 0 || oct24.getDay() === 6 ? 'WEEKEND' : 'WEEKDAY'})`);
          }} className="check-weekend-btn">
            Check Weekend
          </button>
          <button onClick={() => {
            // Debug day 23 specifically
            console.log('=== DEBUGGING DAY 23 ===');
            console.log('Current monthData:', monthData);
            console.log('Looking for 2025-10-23:', monthData['2025-10-23']);
            console.log('Looking for 2025-10-24:', monthData['2025-10-24']);
            
            // Check calendar generation
            const calendar = generateMonthCalendar();
            const day23 = calendar.find(d => d.day === 23);
            const day24 = calendar.find(d => d.day === 24);
            console.log('Day 23 in calendar:', day23);
            console.log('Day 24 in calendar:', day24);
            
            // Check if day 23 is weekend
            if (day23) {
              console.log('Day 23 isWeekend:', day23.isWeekend);
              console.log('Day 23 date:', day23.date);
            }
            
            alert(`Day 23 data: ${monthData['2025-10-23'] ? 'EXISTS' : 'MISSING'}\nDay 24 data: ${monthData['2025-10-24'] ? 'EXISTS' : 'MISSING'}`);
          }} className="debug-day23-btn">
            Debug Day 23
          </button>
          <button onClick={() => {
            // Force clear and re-add data for day 23
            setMonthData({});
            setTimeout(() => {
              setMonthData({
                '2025-10-23': {
                  activities: [{ client: 'Force23', activity: 'Force23' }],
                  absence: { type: 'Présent', telework: '0', restaurantTicket: '0' },
                  dayValue: [1]
                }
              });
              setForceUpdate(prev => prev + 1);
              alert('Force added data for day 23 only');
            }, 100);
          }} className="force-day23-btn">
            Force Day 23
          </button>
          <button onClick={() => {
            // Check date formats
            console.log('=== DATE FORMAT CHECK ===');
            const today = new Date();
            const oct23 = new Date(2025, 9, 23);
            const oct24 = new Date(2025, 9, 24);
            
            console.log('Today ISO:', today.toISOString().split('T')[0]);
            console.log('Oct 23 ISO:', oct23.toISOString().split('T')[0]);
            console.log('Oct 24 ISO:', oct24.toISOString().split('T')[0]);
            
            // Check calendar generation
            const calendar = generateMonthCalendar();
            const day23 = calendar.find(d => d.day === 23);
            const day24 = calendar.find(d => d.day === 24);
            
            console.log('Calendar day 23 date:', day23?.date);
            console.log('Calendar day 24 date:', day24?.date);
            
            // Check what data exists
            console.log('Data for 2025-10-23:', monthData['2025-10-23']);
            console.log('Data for 2025-10-24:', monthData['2025-10-24']);
            
            // Check if dates match
            if (day23) {
              console.log('Day 23 date matches 2025-10-23?', day23.date === '2025-10-23');
              console.log('Day 23 data lookup:', monthData[day23.date]);
            }
            if (day24) {
              console.log('Day 24 date matches 2025-10-24?', day24.date === '2025-10-24');
              console.log('Day 24 data lookup:', monthData[day24.date]);
            }
            
            alert(`Date format check complete. Check console for details.`);
          }} className="check-dates-btn">
            Check Dates
          </button>
        </div>

        {/* Monthly Calendar */}
        <div className="calendar-section">
          <h4>Calendrier du mois</h4>
          <div className="debug-info">
            <p><strong>Données du mois:</strong> {Object.keys(monthData).length} jour(s) enregistré(s)</p>
            {Object.keys(monthData).length > 0 && (
              <div>
                {Object.entries(monthData).map(([date, data]) => (
                  <div key={date} className="day-debug">
                    <strong>{date}:</strong> 
                    <span className="values"> {data.dayValue.map(v => v.toFixed(2)).join(' + ')} = {data.dayValue.reduce((sum, val) => sum + val, 0).toFixed(2)}</span>
                    <span className="clients"> (clients: {data.activities.length})</span>
                  </div>
                ))}
              </div>
            )}
            <div className="calendar-debug">
              <p><strong>Calendar Debug:</strong></p>
              <p>Current month: {currentDate.getMonth() + 1}/{currentDate.getFullYear()}</p>
              <p>Today's date: {new Date().toISOString().split('T')[0]}</p>
              <p>Calendar dates: {calendar.filter(d => d.day).map(d => `${d.day}:${d.date}`).join(', ')}</p>
            </div>
          </div>
          <div className="calendar" key={forceUpdate}>
            <div className="calendar-header">
              {dayNames.map(day => (
                <div key={day} className="day-header">{day}</div>
              ))}
            </div>
            <div className="calendar-grid">
              {calendar.map((dayData, index) => (
                <div
                  key={index}
                  className={`calendar-day ${dayData.isWeekend ? 'weekend' : ''} ${dayData.isToday ? 'today' : ''}`}
                >
                  {dayData.day && (
                    <>
                      <div className="day-number">{dayData.day}</div>
                      {!dayData.isWeekend && (
                        <>
                          <div className="day-separator"></div>
                          {(() => {
                            const dayData_result = getDayData(dayData.date);
                            console.log(`Rendering day ${dayData.day} (${dayData.date}):`, dayData_result);
                            console.log(`Day ${dayData.day} isWeekend:`, dayData.isWeekend);
                            console.log(`Day ${dayData.day} dayOfWeek:`, new Date(dayData.date).getDay());
                            
                            if (dayData_result) {
                              console.log(`✅ Day ${dayData.day} has data, rendering values`);
                              return (
                                <div className="day-values">
                                  {dayData_result.dayValue.map((value, i) => (
                                    <div key={i} className="value-item">
                                      {value.toFixed(2)}
                                    </div>
                                  ))}
                                  <div className="total-value">
                                    Total: {dayData_result.dayValue.reduce((sum, val) => sum + val, 0).toFixed(2)}
                                  </div>
                                </div>
                              );
                            } else {
                              console.log(`❌ Day ${dayData.day} has no data, showing empty`);
                              return <div className="empty-day">-</div>;
                            }
                          })()}
                        </>
                      )}
                    </>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ActivityTracker;
