import React, { useState } from 'react';
import './LoginPage.css';

const LoginPage = ({ onLogin }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    firstName: '',
    lastName: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [isSignUp, setIsSignUp] = useState(false);
  const [isAnimating, setIsAnimating] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (isSignUp) {
      // Sign up validation
      if (formData.password !== formData.confirmPassword) {
        alert('Passwords do not match');
        return;
      }
      if (!formData.firstName || !formData.lastName) {
        alert('Please fill in all required fields');
        return;
      }
    }
    
    // Simple authentication - in real app, this would call an API
    if (formData.email && formData.password) {
      // Check for admin login (simple demo)
      const isAdmin = formData.email.toLowerCase().includes('admin') || 
                     formData.email.toLowerCase().includes('administrateur');
      const role = isAdmin ? 'admin' : 'employee';
      onLogin('dummy-token', role);
    }
  };

  const toggleForm = () => {
    setIsAnimating(true);
    setTimeout(() => {
      setIsSignUp(!isSignUp);
      setFormData({
        email: '',
        password: '',
        confirmPassword: '',
        firstName: '',
        lastName: ''
      });
      setShowPassword(false);
      setIsAnimating(false);
    }, 300);
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h1 className="login-title">{isSignUp ? 'Sign Up' : 'Login'}</h1>
        
        <div className={`form-container ${isAnimating ? 'animating' : ''}`}>
          <form onSubmit={handleSubmit} className="login-form">
            {isSignUp && (
              <>
                <div className="form-group">
                  <label htmlFor="firstName" className="form-label">First Name:</label>
                  <input
                    type="text"
                    id="firstName"
                    name="firstName"
                    value={formData.firstName}
                    onChange={handleChange}
                    className="form-control"
                    placeholder="Enter first name"
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="lastName" className="form-label">Last Name:</label>
                  <input
                    type="text"
                    id="lastName"
                    name="lastName"
                    value={formData.lastName}
                    onChange={handleChange}
                    className="form-control"
                    placeholder="Enter last name"
                    required
                  />
                </div>
              </>
            )}

            <div className="form-group">
              <label htmlFor="email" className="form-label">Email:</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className="form-control"
                placeholder="Enter email"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="password" className="form-label">Password:</label>
              <input
                type={showPassword ? 'text' : 'password'}
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                className="form-control"
                placeholder="Enter password"
                required
              />
            </div>

            {isSignUp && (
              <div className="form-group">
                <label htmlFor="confirmPassword" className="form-label">Confirm Password:</label>
                <input
                  type={showPassword ? 'text' : 'password'}
                  id="confirmPassword"
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  className="form-control"
                  placeholder="Confirm password"
                  required
                />
              </div>
            )}

            <div className="checkbox-group">
              <input
                type="checkbox"
                id="showPassword"
                checked={showPassword}
                onChange={(e) => setShowPassword(e.target.checked)}
                className="checkbox"
              />
              <label htmlFor="showPassword" className="checkbox-label">Show Password</label>
            </div>

            <button type="submit" className="signin-btn">
              {isSignUp ? 'SIGN UP' : 'SIGN IN'}
            </button>
          </form>
        </div>

        <div className="login-links">
          {!isSignUp && (
            <p className="forgot-link">
              Forgot <span className="link-text">Username / Password</span>?
            </p>
          )}
          <p className="signup-link">
            {isSignUp ? 'Already have an account?' : "Don't have an account?"} 
            <span className="link-text" onClick={toggleForm}>
              {isSignUp ? ' Sign in' : ' Sign up'}
            </span>
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
