import React, { useState, useEffect } from 'react';
import './AdminDashboard.css';
import { getAllUsers } from '../firebase/firestoreService';
import { exportEmployeeDataToCSV, downloadCSV } from '../firebase/exportService';

const AdminDashboard = ({ onLogout }) => {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [exporting, setExporting] = useState({});
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    loadEmployees();
  }, []);

  const loadEmployees = async () => {
    try {
      const result = await getAllUsers();
      if (result.success) {
        setEmployees(result.users);
      } else {
        console.error('Error loading employees:', result.error);
        // Fallback to mock data if Firestore fails
        setEmployees([
        {
          id: 1,
          name: 'Jean Dupont',
          email: 'jean.dupont@company.com',
          phone: '+33 1 23 45 67 89',
          address: '123 Rue de la Paix, 75001 Paris',
          department: 'IT',
          position: 'Développeur Senior',
          status: 'active'
        },
        {
          id: 2,
          name: 'Marie Martin',
          email: 'marie.martin@company.com',
          phone: '+33 1 23 45 67 90',
          address: '456 Avenue des Champs, 75008 Paris',
          department: 'Marketing',
          position: 'Chef de Projet',
          status: 'active'
        },
        {
          id: 3,
          name: 'Pierre Durand',
          email: 'pierre.durand@company.com',
          phone: '+33 1 23 45 67 91',
          address: '789 Boulevard Saint-Germain, 75006 Paris',
          department: 'Ventes',
          position: 'Commercial',
          status: 'inactive'
        },
        {
          id: 4,
          name: 'Sophie Bernard',
          email: 'sophie.bernard@company.com',
          phone: '+33 1 23 45 67 92',
          address: '321 Rue de Rivoli, 75004 Paris',
          department: 'RH',
          position: 'Responsable RH',
          status: 'active'
        },
        {
          id: 5,
          name: 'Lucas Moreau',
          email: 'lucas.moreau@company.com',
          phone: '+33 1 23 45 67 93',
          address: '654 Place de la République, 75011 Paris',
          department: 'IT',
          position: 'Développeur Junior',
          status: 'inactive'
        },
        {
          id: 6,
          name: 'Emma Petit',
          email: 'emma.petit@company.com',
          phone: '+33 1 23 45 67 94',
          address: '987 Rue de la Roquette, 75011 Paris',
          department: 'Finance',
          position: 'Comptable',
          status: 'active'
        }
      ]);
      }
    } catch (error) {
      console.error('Error loading employees:', error);
      // Fallback to mock data
      setEmployees([
        {
          id: 1,
          name: 'Jean Dupont',
          email: 'jean.dupont@company.com',
          phone: '+33 1 23 45 67 89',
          address: '123 Rue de la Paix, 75001 Paris',
          department: 'IT',
          position: 'Développeur Senior',
          status: 'active'
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleExportCSV = async (employee) => {
    try {
      setExporting(prev => ({ ...prev, [employee.id]: true }));
      setSuccessMessage('');

      const result = await exportEmployeeDataToCSV(employee.id, employee.name);

      if (result.success) {
        if (result.data.length === 0) {
          alert('Aucune donnée trouvée pour cet employé');
          return;
        }

        const filename = `${employee.name.replace(/\s+/g, '_')}_activites.csv`;
        downloadCSV(result.data, filename);

        setSuccessMessage(`Données exportées avec succès pour ${employee.name}`);
        setTimeout(() => setSuccessMessage(''), 3000);
      } else {
        alert('Erreur lors de l\'export: ' + result.error);
      }
    } catch (error) {
      console.error('Export error:', error);
      alert('Erreur lors de l\'export des données');
    } finally {
      setExporting(prev => ({ ...prev, [employee.id]: false }));
    }
  };

  const getStatusColor = (status) => {
    return status === 'active' ? '#20b2aa' : '#dc3545';
  };

  const getStatusText = (status) => {
    return status === 'active' ? 'Actif' : 'Inactif';
  };

  if (loading) {
    return (
      <div className="admin-dashboard">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Chargement des employés...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      <header className="admin-header">
        <div className="header-content">
          <h1>Tableau de Bord Administrateur</h1>
          <div className="header-actions">
            <button onClick={handleExportCSV} className="export-btn">
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
                <polyline points="7,10 12,15 17,10"></polyline>
                <line x1="12" y1="15" x2="12" y2="3"></line>
              </svg>
              Exporter CSV
            </button>
            <button onClick={onLogout} className="logout-btn">
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4"></path>
                <polyline points="16,17 21,12 16,7"></polyline>
                <line x1="21" y1="12" x2="9" y2="12"></line>
              </svg>
              Déconnexion
            </button>
          </div>
        </div>
      </header>

      <div className="admin-content">
        {successMessage && (
          <div className="success-message">
            {successMessage}
          </div>
        )}

        <div className="stats-overview">
          <div className="stat-card">
            <div className="stat-icon">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path>
                <circle cx="12" cy="7" r="4"></circle>
              </svg>
            </div>
            <div className="stat-content">
              <h3>{employees.length}</h3>
              <p>Total Employés</p>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M9 12l2 2 4-4"></path>
                <path d="M21 12c-1 0-3-1-3-3s2-3 3-3 3 1 3 3-2 3-3 3"></path>
                <path d="M3 12c1 0 3-1 3-3s-2-3-3-3-3 1-3 3 2 3 3 3"></path>
              </svg>
            </div>
            <div className="stat-content">
              <h3>{employees.filter(emp => emp.status === 'active').length}</h3>
              <p>Employés Actifs</p>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M9 12l2 2 4-4"></path>
                <path d="M21 12c-1 0-3-1-3-3s2-3 3-3 3 1 3 3-2 3-3 3"></path>
                <path d="M3 12c1 0 3-1 3-3s-2-3-3-3-3 1-3 3 2 3 3 3"></path>
              </svg>
            </div>
            <div className="stat-content">
              <h3>{employees.filter(emp => emp.status === 'inactive').length}</h3>
              <p>Employés Inactifs</p>
            </div>
          </div>
        </div>

        <div className="employees-section">
          <h2>Liste des Employés</h2>
          <div className="employees-grid">
            {employees.map(employee => (
              <div key={employee.id} className="employee-card">
                <div className="card-header">
                  <div className="employee-avatar">
                    {employee.name.split(' ').map(n => n[0]).join('')}
                  </div>
                  <div className="employee-info">
                    <h3>{employee.name}</h3>
                    <div className="employee-status">
                      <span 
                        className="status-badge"
                        style={{ backgroundColor: getStatusColor(employee.status) }}
                      >
                        {getStatusText(employee.status)}
                      </span>
                    </div>
                  </div>
                </div>

                <div className="card-content">
                  <div className="personal-details">
                    <h4>Détails Personnels</h4>
                    <div className="detail-item">
                      <strong>Email:</strong> {employee.email}
                    </div>
                    <div className="detail-item">
                      <strong>Téléphone:</strong> {employee.phone}
                    </div>
                    <div className="detail-item">
                      <strong>Adresse:</strong> {employee.address}
                    </div>
                    <div className="detail-item">
                      <strong>Département:</strong> {employee.department}
                    </div>
                    <div className="detail-item">
                      <strong>Poste:</strong> {employee.position}
                    </div>
                  </div>
                </div>

                <div className="card-actions">
                  <button 
                    onClick={() => handleExportCSV(employee)} 
                    className="export-btn"
                    disabled={exporting[employee.id]}
                  >
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
                      <polyline points="7,10 12,15 17,10"></polyline>
                      <line x1="12" y1="15" x2="12" y2="3"></line>
                    </svg>
                    {exporting[employee.id] ? 'Exportation...' : 'Exporter CSV'}
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
