import React, { useState } from 'react';
import './LoginPage.css';
import { registerUser, signInUser } from '../firebase/authService';

const LoginPage = ({ onLogin }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    firstName: '',
    lastName: '',
    position: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [isSignUp, setIsSignUp] = useState(false);
  const [isAnimating, setIsAnimating] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    try {
      if (isSignUp) {
        // Sign up validation
        if (formData.password !== formData.confirmPassword) {
          setError('Passwords do not match');
          setIsLoading(false);
          return;
        }
        if (!formData.firstName || !formData.lastName || !formData.position) {
          setError('Please fill in all required fields');
          setIsLoading(false);
          return;
        }

        // Register user with Firebase
        const result = await registerUser({
          email: formData.email,
          password: formData.password,
          firstName: formData.firstName,
          lastName: formData.lastName,
          position: formData.position
        });

        if (result.success) {
          onLogin(result.user);
        } else {
          setError(result.error);
        }
      } else {
        // Sign in with Firebase
        const result = await signInUser(formData.email, formData.password);

        if (result.success) {
          onLogin(result.user);
        } else {
          setError(result.error);
        }
      }
    } catch (error) {
      setError('An unexpected error occurred. Please try again.');
      console.error('Authentication error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const toggleForm = () => {
    setIsAnimating(true);
    setError(''); // Clear any existing errors
    setTimeout(() => {
      setIsSignUp(!isSignUp);
      setFormData({
        email: '',
        password: '',
        confirmPassword: '',
        firstName: '',
        lastName: '',
        position: ''
      });
      setShowPassword(false);
      setIsAnimating(false);
    }, 300);
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h1 className="login-title">{isSignUp ? 'Sign Up' : 'Login'}</h1>

        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        <div className={`form-container ${isAnimating ? 'animating' : ''}`}>
          <form onSubmit={handleSubmit} className="login-form">
            {isSignUp && (
              <>
                <div className="form-group">
                  <label htmlFor="firstName" className="form-label">First Name:</label>
                  <input
                    type="text"
                    id="firstName"
                    name="firstName"
                    value={formData.firstName}
                    onChange={handleChange}
                    className="form-control"
                    placeholder="Enter first name"
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="lastName" className="form-label">Last Name:</label>
                  <input
                    type="text"
                    id="lastName"
                    name="lastName"
                    value={formData.lastName}
                    onChange={handleChange}
                    className="form-control"
                    placeholder="Enter last name"
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="position" className="form-label">Poste:</label>
                  <select
                    id="position"
                    name="position"
                    value={formData.position}
                    onChange={handleChange}
                    className="form-control"
                    required
                  >
                    <option value="">SÃ©lectionner un poste</option>
                    <option value="DÃ©veloppeur Senior">DÃ©veloppeur Senior</option>
                    <option value="DÃ©veloppeur Junior">DÃ©veloppeur Junior</option>
                    <option value="Chef de Projet">Chef de Projet</option>
                    <option value="Commercial">Commercial</option>
                    <option value="Responsable RH">Responsable RH</option>
                    <option value="Comptable">Comptable</option>
                    <option value="Chef d'Ã©quipe">Chef d'Ã©quipe</option>
                    <option value="Analyste">Analyste</option>
                    <option value="Consultant">Consultant</option>
                    <option value="Manager">Manager</option>
                    <option value="Autre">Autre</option>
                  </select>
                </div>
              </>
            )}

            <div className="form-group">
              <label htmlFor="email" className="form-label">Email:</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className="form-control"
                placeholder="Enter email"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="password" className="form-label">Password:</label>
              <input
                type={showPassword ? 'text' : 'password'}
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                className="form-control"
                placeholder="Enter password"
                required
              />
            </div>

            {isSignUp && (
              <div className="form-group">
                <label htmlFor="confirmPassword" className="form-label">Confirm Password:</label>
                <input
                  type={showPassword ? 'text' : 'password'}
                  id="confirmPassword"
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  className="form-control"
                  placeholder="Confirm password"
                  required
                />
              </div>
            )}

            <div className="checkbox-group">
              <input
                type="checkbox"
                id="showPassword"
                checked={showPassword}
                onChange={(e) => setShowPassword(e.target.checked)}
                className="checkbox"
              />
              <label htmlFor="showPassword" className="checkbox-label">Show Password</label>
            </div>

            <button type="submit" className="signin-btn" disabled={isLoading}>
              {isLoading ? 'Loading...' : (isSignUp ? 'SIGN UP' : 'SIGN IN')}
            </button>
          </form>
        </div>

        <div className="login-links">
          {!isSignUp && (
            <p className="forgot-link">
              Forgot <span className="link-text">Username / Password</span>?
            </p>
          )}
          <p className="signup-link">
            {isSignUp ? 'Already have an account?' : "Don't have an account?"} 
            <span className="link-text" onClick={toggleForm}>
              {isSignUp ? ' Sign in' : ' Sign up'}
            </span>
          </p>
          <p className="admin-hint">
            <small>ðŸ’¡ Admin access: Use email containing "admin" or "administrateur"</small>
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
